import { getAllByAltText } from '@testing-library/react';
import './ExpenseForm.css';
import { useState } from 'react';
const ExpenseForm = () => {
    const submitHandler = (event) => {
        event.preventDefault();
        const ExpenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate)
        }
    }
    //**************maintaining individual useState*************
    const [enteredTitle, setEndteredTitle] = useState('');
    const [enteredAmount, setEndteredAmount] = useState('');
    const [enteredDate, setEndteredDate] = useState('');
    const titleChangeHandler = (event) => {
        setEndteredTitle(event.target.value);
    };
    const amountChangeHandler = (event) => {
        setEndteredAmount(event.target.value);
    };
    const dateChangeHandler = (event) => {
        setEndteredDate(event.target.value);
    };

    // if we want to avoid multiple change handler function, we can write one fucntion for all
    // and call that fucntion manually on event.
    // const inputChangeHandler = (type, value) => {
    //     if(type === 'title'){
    //         setEndteredTitle(value);
    //     }else if(type === 'amount'){
    //         setEndteredAmount(value);
    //     }else{
    //         setEndteredDate(value);
    //     }
    // }

    /**************keeing all state as an object to useState *********/
    // const [userInput, setUserInput] = useState({
    //     enteredDate: '',
    //     enteredAmount: '',
    //     enteredTitle: ''
    // });
    // const titleChangeHandler = (event) => {
    //     setUserInput({
    //         // ... -> this is the spread operator. 
    //         // It is used to maintain the value of other variables in object.
    //         ...userInput,
    //         enteredTitle: event.target.value
    //     })
    // };
    // const amountChangeHandler = (event) => {
    //     setUserInput({
    //         ...userInput,
    //         enteredAmount: event.target.value
    //     })
    // };
    // const dateChangeHandler = (event) => {
    //     setUserInput({
    //         ...userInput,
    //         enteredDate: event.target.value
    //     })
    // };
    // React schedules the state update. In case we want to update multiple states it can we a case that 
    // update gets performed on some old state. To make the process sync, we should try to use retrun form
    // setUserInput function. Example:
    // so if u r dependent on previous state to update the current state, use this approach:
    // setUserInput((prevState) =>{
    //     return(...prevState,
    //         enteredTitle: event.target.value);
    // });

    return(
        <form onSubmit={submitHandler}>
            <div className='new-expense__controls'>
                <div className='new-expense__control'>
                    <label>Title</label>
                    <input type='text' onChange={titleChangeHandler}/>
                    {/* <input type='text' onChange={(event) => inputChangeHandler('title',event.target.value)}/> */}
                </div>
                <div className='new-expense__control'>
                    <label>Amount</label>
                    <input type='number' min='0.01' step='0.01' onChange={amountChangeHandler}/>
                    {/* <input type='number' onChange={(event) => inputChangeHandler('amount',event.target.value)}/> */}
                </div>
                <div className='new-expense__control'>
                    <label>Date</label>
                    <input type='date' min='2021-01-01' max='2023-12-31' onChange={dateChangeHandler}/>
                    {/* <input type='date' onChange={(event) => inputChangeHandler('date',event.target.value)}/> */}
                </div>
            </div>
            <div className='new-expense__actions'>
                <button type='submit'>Add Expense</button>
            </div>
        </form>
    );
}

export default ExpenseForm;
